with open("file.txt") as configData:
    pyresponse = json.loads(configData.read())





snd_addr = pyresponse["senderAddress"]
prvt_ky = pyresponse["privateKeyBar"]
bnb_amo = float(pyresponse["bnbAmount"])
#add = '0x079Dd74Cc214Ac5f892f6a7271ef0722F6D0c2e6'

sender_address = str(snd_addr)
my_private_key = str(prvt_ky)
bnb_amount = bnb_amo

balance = web3.eth.get_balance(sender_address)
#print(balance)
 
humanReadable = web3.fromWei(balance,'ether')
txt1 = "{} --- It is your Smart Chain Wallet Address".format(sender_address)
print(txt1)
#print(sender_address)
txt2 = "{} --- available BNB amount to use in your WALLET".format(humanReadable)
print(txt2)
#print(humanReadable)
txt3 = "{} BNB will buy".format(bnb_amount)
print(txt3)
#print(bnb_amount)

#Contract Address of Token we want to buy
tokenToBuy = web3.toChecksumAddress(input("Enter TokenAddress: "))            #web3.toChecksumAddress("0x6615a63c260be84974166a5eddff223ce292cf3d")
#print(tokenToBuy)

spend = web3.toChecksumAddress("0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c")  #wbnb contract
 
#Setup the PancakeSwap contract
contract = web3.eth.contract(address=panRouterContractAddress, abi=panabi)
 

nonce = web3.eth.get_transaction_count(sender_address)
 
start = time.time()

amountIn = web3.toWei(bnb_amount,'ether')
amounts = contract.all_functions.getAmountsOut(amountIn, [spend, tokenToBuy]);
amountOutMin = amounts[1].sub(amounts[1].div(12))

pancakeswap2_txn = contract.functions.swapExactTokensForTokens(
    amountIn,
    amountOutMin,
    [spend, tokenToBuy],
    sender_address,
    (int(time.time()) + 10000)).buildTransaction({
        'gas': 250000,
        'gasPrice': web3.toWei('5','gwei'),
        'nonce': nonce,
    })
    #gasprice 100 gwei
    #gas 500000
    
signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key=my_private_key)
tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
print(web3.toHex(tx_token))
      
